CREATE TABLE IF NOT EXISTS SPRING_AI_CHAT_MEMORY ( `id` int(50) not null COMMENT 'id', `conversation_id` VARCHAR(36) NOT NULL COMMENT '会话ID，唯一标识一次对话', `content` TEXT NOT NULL COMMENT '消息内容', `type` VARCHAR(10) NOT NULL COMMENT '消息类型', `timestamp` TIMESTAMP NOT NULL COMMENT '消息时间戳', PRIMARY KEY (`id`), INDEX `idx_conversation_id_timestamp` (`conversation_id`, `timestamp`) ) COMMENT 'AI对话记忆存储表'